import { useState } from "react";
import { Link } from "react-router-dom";
export default function Navbar() {

  const [buttonFormationDisabled, setButtonFormationDisabled] = useState(false);
  const [buttonExperiencesDisabled, setButtonPrestationsDisabled] = useState(false);
  const [ButtonRealisationDisabled, setButtonContactDisabled] = useState(false);
  const [ButtonCompetencesDisabled, setButtonBlogDisabled] = useState(false);


  return (
    <div className="navbar">
      <Link to={'/index'} onClick={() => { window.scrollTo({ top: 0, left: 0 }) }}>
        <button disabled={buttonFormationDisabled}
          onClick={() => { setButtonFormationDisabled(true); setButtonPrestationsDisabled(false); setButtonContactDisabled(false); setButtonBlogDisabled(false) }}>
          Formation
        </button></Link>
      <Link to={'/prestations'} onClick={() => { window.scrollTo({ top: 0, left: 0 }) }}>
        <button disabled={buttonExperiencesDisabled}
          onClick={() => { setButtonPrestationsDisabled(true); setButtonContactDisabled(false); setButtonFormationDisabled(false); setButtonBlogDisabled(false) }}>
          Experiences
        </button></Link>
      <Link to={'/blog'} onClick={() => { window.scrollTo({ top: 0, left: 0 }) }}>
        <button disabled={ButtonCompetencesDisabled}
          onClick={() => { setButtonContactDisabled(false); setButtonPrestationsDisabled(false); setButtonFormationDisabled(false); setButtonBlogDisabled(true) }}>
          Competences
        </button></Link>
      <Link to={'/contact'} onClick={() => { window.scrollTo({ top: 0, left: 0 }) }}>
        <button disabled={ButtonRealisationDisabled}
          onClick={() => { setButtonContactDisabled(true); setButtonPrestationsDisabled(false); setButtonFormationDisabled(false); setButtonBlogDisabled(false) }}>
          Realisations
        </button></Link>
    </div>
  );
}
